      </label>

      <label>
        Price:
        <input type="text" name="price" onChange={e=>setPrice(e.target.value)}/>
      </label>

      <label>
        Stock:
        <input type="text" name="stock" onChange={e=>setPrice(e.target.value)}/>
      </label>

      <label>
        Health benefit:
        <textarea type="text" name="health_benefit" onChange={e=>setPrice(e.target.value)}/>
      </label>

      <button type="submit">Add to List</button>
    </form>
  );
}

export default NewFruitForm;
