import React, { useEffect, useState } from "react";
import {useParams, useHistory} from "react-router-dom"
import FruitList from "./FruitList";
import background from "./images/Peach.png"
function Shop() {
  const {ownerId} = useParams()
  const history = useHistory() 
  const [fruits, setFruits] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetch(`http://localhost:9292/owners/${ownerId}/fruits`)
    .then((r) => r.json())
    .then((fruits) => {
      console.log(fruits)
      setFruits(fruits)
    })
  },[])

  function handleAddFruit(newFruit) {
    setFruits([...fruits, newFruit])
  }

  function handleDeleteFruit(id) {
    const updatedFruits = fruits.filter((fruit) => fruit.id !== id)
    setFruits(updatedFruits)
  }

  function handleUpdateFruit(updatedFruit) {
    const updatedFruits = fruits.map((fruit) => {
      if (fruit.id === updatedFruit.id) {
        return updatedFruit;
      } else {
        return fruit;
      }
    });
    setFruits(updatedFruits)
  }

  const showFruits = fruits.filter((fruit) =>
    fruit.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
   <div style={{backgroundImage: `url(${background})`}}>
      <FruitList
        fruits={showFruits}
        onDeleteFruit={handleDeleteFruit}
      />
    </div>
  );
}

export default Shop;
