import React, {useState} from "react";

function NewFruitForm({onAddFruit, selectedOwner}) {
  const [formData, setFormData] = useState({
      name: "",
      price: 0,
      stock: 0,
      health_benefit: "",
      owner_id: selectedOwner
  })

  function handleChange(e){
