  const [fruits, setFruits] = useState([]);
  const [search, setSearch] = useState("");


  fetch(`http://localhost:9292/owners/${ownerId}/fruits`)
  .then((r) => r.json())
  .then((fruits) => setFruits(fruits))

  function handleAddFruit(newFruit) {
    setFruits([...fruits, newFruit])
  }

